import React, { useState } from "react";
import Header from "./Header";
function Table({ data }) {
    const column = ['id', 'title', 'description', 'price', 'discountPercentage', 'rating', 'stock', 'brand', 'category'];
    const [sortColumn, setSortColumn] = useState(null);
    const [sortOrder, setSortOrder] = useState("asc");
    const [searchQuery, setSearchQuery] = useState("");
    const [debounceQuery, setDebounceQuery] = useState("");
    const [currentPage, setCurrentPage] = useState(1);
    const [resultsPerPage, setResultsPerPage] = useState(10);
    const [selectedColumns, setSelectedColumns] = useState([...column].reduce((acc, val) => { acc[val] = true; return acc; }, {}));
    const [priceFilterEnabled, setPriceFilterEnabled] = useState(false);
    const [minPrice, setMinPrice] = useState(0);
    const [maxPrice, setMaxPrice] = useState(1000);

    const handleColumnChange = (columnName) => {
        setSelectedColumns(prevState => ({
            ...prevState,
            [columnName]: !prevState[columnName]
        }));
    };

    const handleSort = (columnName) => {
        if (sortColumn === columnName) {
            setSortOrder(sortOrder === "asc" ? "desc" : "asc");
        } else {
            setSortColumn(columnName);
            setSortOrder("asc");
        }
    };

    const debounce = (func, delay) => {
        let timeoutId;
        return function (...args) {
            clearTimeout(timeoutId);
            timeoutId = setTimeout(() => func.apply(this, args), delay);
        };
    };

    const debouncedHandleSearch = debounce((value) => {
        setDebounceQuery(value);
    }, 500);

    const handleSearch = (event) => {
        const value = event.target.value;
        setSearchQuery(value);
        debouncedHandleSearch(value);
    };

    const handlePageChange = (page) => {
        setCurrentPage(page);
    };

    const handleResultsPerPageChange = (event) => {
        setResultsPerPage(parseInt(event.target.value, 10));
        setCurrentPage(1); // Reset to the first page when changing results per page
    };

    const totalResults = data?.length;
    const totalPages = Math.ceil(totalResults / resultsPerPage);
    const startIndex = (currentPage - 1) * resultsPerPage;
    const endIndex = startIndex + resultsPerPage;
    const currentPageData = data?.slice(startIndex, endIndex);

    const filteredData = currentPageData?.filter((item) =>
        Object.values(item).some((value) =>
            new RegExp(debounceQuery, "i").test(value)
        )
    );

    const sortedData = filteredData?.slice().sort((a, b) => {
        if (sortColumn) {
            if (sortOrder === "asc") {
                return a[sortColumn] > b[sortColumn] ? 1 : -1;
            } else {
                return a[sortColumn] < b[sortColumn] ? 1 : -1;
            }
        }
        return 0;
    });
    const priceFilteredData = priceFilterEnabled
        ? sortedData?.filter(item => item.price >= minPrice && item.price <= maxPrice)
        : sortedData;

    return (
        <div>
            <Header/>
            <div>
                <label>
                    Entries per page:
                    <select value={resultsPerPage} onChange={handleResultsPerPageChange}>
                        <option value={5}>5</option>
                        <option value={10}>10</option>
                        <option value={20}>20</option>
                    </select>
                </label>
            </div>
            <div>
                <label>Select columns to display:</label>
                {column.map((columnName) => (
                    <label key={columnName}>
                        <input
                            type="checkbox"
                            value={columnName}
                            checked={selectedColumns[columnName]}
                            onChange={() => handleColumnChange(columnName)}
                        />
                        {columnName}
                    </label>
                ))}
            </div>
            <div>
                <label>
                    <input
                        type="checkbox"
                        checked={priceFilterEnabled}
                        onChange={(e) => setPriceFilterEnabled(e.target.checked)}
                    />
                    Price Range:
                </label>
                {priceFilterEnabled && (
                    <div>
                        <input
                            type="number"
                            placeholder="Min Price"
                            value={minPrice}
                            onChange={(e) => setMinPrice(parseInt(e.target.value))}
                        />
                        <input
                            type="number"
                            placeholder="Max Price"
                            value={maxPrice}
                            onChange={(e) => setMaxPrice(parseInt(e.target.value))}
                        />
                    </div>
                )}
            </div>
            <input
                type="text"
                placeholder="Search..."
                value={searchQuery}
                onChange={handleSearch}
            />
            {data ? (
                <div>
                    <table>
                        <thead>
                            <tr>
                                {Object.entries(selectedColumns).map(([columnName, isChecked]) => isChecked && (
                                    <th key={columnName} onClick={() => handleSort(columnName)}>{columnName}</th>
                                ))}
                            </tr>
                        </thead>
                        <tbody>
                            {priceFilteredData?.map((item) => (
                                <tr key={item.id}>
                                    {Object.entries(selectedColumns).map(([columnName, isChecked]) => isChecked && (
                                        <td key={columnName}>{item[columnName]}</td>
                                    ))}
                                </tr>
                            ))}
                        </tbody>
                    </table>
                    <div>
                        <button
                            onClick={() => handlePageChange(currentPage - 1)}
                            disabled={currentPage === 1}
                        >
                            Previous
                        </button>
                        Page {currentPage} of {totalPages}
                        <button
                            onClick={() => handlePageChange(currentPage + 1)}
                            disabled={currentPage === totalPages}
                        >
                            Next
                        </button>
                    </div>
                </div>
            ) : (
                <p>Loading...</p>
            )}
        </div>
    );
}

export default Table;
