import { useState } from "react";
function Table({ data }) {
    console.log(data);
    const [column, setColumn] = useState(['id', 'title', 'description', 'price', 'discountPercentage', 'rating', 'stock', 'brand', 'category']);
    const [sortColumn, setSortColumn] = useState(null);
    const [sortOrder, setSortOrder] = useState("asc");
    const [searchQuery, setSearchQuery] = useState("");
    const [debounceQuery, setDebounceQuery] = useState("");
    const [currentPage, setCurrentPage] = useState(1);
    const [resultsPerPage, setResultsPerPage] = useState(10);
    const [selectedColumns, setSelectedColumns] = useState([...column]);

    const handleColumnChange = (event) => {
        const selectedOptions = Array.from(event.target.selectedOptions).map(
            (option) => option.value
        );
        setSelectedColumns(selectedOptions);
    };

    const handleSort = (columnName) => {
        if (sortColumn === columnName) {
            setSortOrder(sortOrder === "asc" ? "desc" : "asc");
        } else {
            setSortColumn(columnName);
            setSortOrder("asc");
        }
    };
    const debounce = (func, delay) => {
        let timeoutId;
        return function (...args) {
            clearTimeout(timeoutId);
            timeoutId = setTimeout(() => func.apply(this, args), delay);
        };
    };

    const debouncedHandleSearch = debounce((value) => {
        setDebounceQuery(value);
    }, 500);

    const handleSearch = (event) => {
        const value = event.target.value;
        setSearchQuery(value);
        debouncedHandleSearch(value);
    };

    const handlePageChange = (page) => {
        setCurrentPage(page);
    };
    const handleResultsPerPageChange = (event) => {
        setResultsPerPage(parseInt(event.target.value, 10));
        setCurrentPage(1); // Reset to the first page when changing results per page
    };

    const totalResults = data?.length;
    const totalPages = Math.ceil(totalResults / resultsPerPage);
    const startIndex = (currentPage - 1) * resultsPerPage;
    const endIndex = startIndex + resultsPerPage;
    const currentPageData = data?.slice(startIndex, endIndex);

    const filteredData = currentPageData?.filter((item) =>
        Object.values(item).some((value) =>
            new RegExp(debounceQuery, "i").test(value)
        )
    );



    const sortedData = filteredData?.slice().sort((a, b) => {
        if (sortColumn) {
            if (sortOrder === "asc") {
                return a[sortColumn] > b[sortColumn] ? 1 : -1;
            } else {
                return a[sortColumn] < b[sortColumn] ? 1 : -1;
            }
        }
        return 0;
    });


    return (
        <div>
            <div>
                <label>
                    Entries per page:
                    <select value={resultsPerPage} onChange={handleResultsPerPageChange}>
                        <option value={5}>5</option>
                        <option value={10}>10</option>
                        <option value={20}>20</option>
                    </select>
                </label>
            </div>
            <div>
            <label>
                Select columns to display:
                <select
                    multiple
                    value={selectedColumns}
                    onChange={handleColumnChange}
                >
                    {column.map((column) => (
                        <option key={column} value={column}>
                            {column}
                        </option>
                    ))}
                </select>
            </label>
            </div>
            <input
                type="text"
                placeholder="Search..."
                value={searchQuery}
                onChange={handleSearch}
            />
            {data ? (
                <div>
                    <table>
                        <thead>
                            <tr>
                                {selectedColumns?.map((item) => (
                                    <th key={item} onClick={() => handleSort(item)}>{item}</th>
                                ))}
                            </tr>
                        </thead>
                        <tbody>
                            {sortedData?.map((item) => (<tr key={item.id}>
                                {selectedColumns.map((col) => {
                                    if (Object.keys(item).includes(col)) {
                                        return <td key={col}>{item[col]}</td>;
                                    }
                                    return null;
                                })}
                            </tr>
                            ))}
                        </tbody>
                    </table>
                    <div>

                        <div>
                            <button
                                onClick={() => handlePageChange(currentPage - 1)}
                                disabled={currentPage === 1}
                            >
                                Previous
                            </button>
                            Page {currentPage} of {totalPages}
                            <button
                                onClick={() => handlePageChange(currentPage + 1)}
                                disabled={currentPage === totalPages}
                            >
                                Next
                            </button>
                        </div>
                    </div>
                </div>



            ) : (
                <p>Loading...</p>
            )}
        </div>
    );
}

export default Table;
