import { useState } from "react";
function Table({ data }) {
    console.log(data);
    const [column, setColumn] = useState(['id', 'title', 'description', 'price', 'discountPercentage', 'rating', 'stock', 'brand', 'category']);
    const [sortColumn, setSortColumn] = useState(null);
    const [sortOrder, setSortOrder] = useState("asc");
    const [searchQuery, setSearchQuery] = useState("");
    const [debounceQuery, setDebounceQuery] = useState("");


    const handleSort = (columnName) => {
        if (sortColumn === columnName) {
            setSortOrder(sortOrder === "asc" ? "desc" : "asc");
        } else {
            setSortColumn(columnName);
            setSortOrder("asc");
        }
    };
    const debounce = (func, delay) => {
        let timeoutId;
        return function (...args) {
            clearTimeout(timeoutId);
            timeoutId = setTimeout(() => func.apply(this, args), delay);
        };
    };

    const debouncedHandleSearch = debounce((value) => {
        setDebounceQuery(value);
    }, 500);

    const handleSearch = (event) => {
        const value = event.target.value;
        setSearchQuery(value);
        debouncedHandleSearch(value);
    };

    const filteredData = data.filter((item) =>
        Object.values(item).some((value) =>
            new RegExp(debounceQuery, "i").test(value)
        )
    );

    const sortedData = filteredData.slice().sort((a, b) => {
        if (sortColumn) {
            if (sortOrder === "asc") {
                return a[sortColumn] > b[sortColumn] ? 1 : -1;
            } else {
                return a[sortColumn] < b[sortColumn] ? 1 : -1;
            }
        }
        return 0;
    });

    return (
        <div>
            <input
                type="text"
                placeholder="Search..."
                value={searchQuery}
                onChange={handleSearch}
            />
            {data ? (
                <table>
                    <thead>
                        <tr>
                            {column?.map((item) => (
                                <th key={item} onClick={() => handleSort(item)}>{item}</th>
                            ))}
                        </tr>
                    </thead>
                    <tbody>
                        {sortedData?.map((item) => (<tr key={item.id}>
                            {column.map((col) => {
                                if (Object.keys(item).includes(col)) {
                                    return <td key={col}>{item[col]}</td>;
                                }
                                return null;
                            })}
                        </tr>
                        ))}
                    </tbody>
                </table>
            ) : (
                <p>Loading...</p>
            )}
        </div>
    );
}

export default Table;
