import React from 'react'
import { useState } from 'react';
const Header = ({ resultsPerPage, setResultsPerPage,
    setCurrentPage, selectedColumns, setSelectedColumns, setPriceFilterEnabled, setMinPrice,
    setMaxPrice, minPrice, maxPrice, priceFilterEnabled }) => {
        const [searchQuery, setSearchQuery] = useState("");
    const column = ['id', 'title', 'description', 'price', 'discountPercentage', 'rating', 'stock', 'brand', 'category'];

    const handleResultsPerPageChange = (event) => {
        setResultsPerPage(parseInt(event.target.value, 10));
        setCurrentPage(1);
    };

    const handleColumnChange = (columnName) => {
        setSelectedColumns(prevState => ({
            ...prevState,
            [columnName]: !prevState[columnName]
        }));
    };
    const debouncedHandleSearch = debounce((value) => {
        setDebounceQuery(value);
    }, 500);

    const handleSearch = (event) => {
        const value = event.target.value;
        setSearchQuery(value);
        debouncedHandleSearch(value);
    };

    return (
        <div>
            <div>
                <label>
                    Entries per page:
                    <select value={resultsPerPage} onChange={handleResultsPerPageChange}>
                        <option value={5}>5</option>
                        <option value={10}>10</option>
                        <option value={20}>20</option>
                    </select>
                </label>
            </div>
            <div>
                <label>Select columns to display:</label>
                {column.map((columnName) => (
                    <label key={columnName}>
                        <input
                            type="checkbox"
                            value={columnName}
                            checked={selectedColumns[columnName]}
                            onChange={() => handleColumnChange(columnName)}
                        />
                        {columnName}
                    </label>
                ))}
            </div>
            <div>
                <label>
                    <input
                        type="checkbox"
                        checked={priceFilterEnabled}
                        onChange={(e) => setPriceFilterEnabled(e.target.checked)}
                    />
                    Price Range:
                </label>
                {priceFilterEnabled && (
                    <div>
                        <input
                            type="number"
                            placeholder="Min Price"
                            value={minPrice}
                            onChange={(e) => setMinPrice(parseInt(e.target.value))}
                        />
                        <input
                            type="number"
                            placeholder="Max Price"
                            value={maxPrice}
                            onChange={(e) => setMaxPrice(parseInt(e.target.value))}
                        />
                    </div>
                )}
            </div>
            <input
                type="text"
                placeholder="Search..."
                value={searchQuery}
                onChange={handleSearch}
            />
        </div>
    )
}

export default Header
